// Example database connection code
$hostname = "localhost"; // Change this to your actual hostname
$username = "roots"; // Change this to your actual username
$password = "1234"; // Change this to your actual password
$database = "seaways"; // Change this to your actual database name

// Create a database connection
$connection = mysqli_connect($hostname, $username, $password, $database);

// Check if the connection was successful
if (!$connection) {
    die("Database connection failed: " . mysqli_connect_error());
}

// if (!isset($_SESSION['username'])) {
//     // Redirect to the login page or display an error message
//     header("Location: login.html");
//     exit;
// }

// Fetch the logged-in seafarer's details
//$seafarerID = $_SESSION['username'];

$sql = "SELECT * FROM seafarers_login WHERE EmpID = 20 ";
$result = mysqli_query($connection, $sql);

if ($row = mysqli_fetch_assoc($result)) {
    $seafarerName = $row['username'];
    $contractType = $row['contractType'];
    $contractEnd = $row['contractEnd'];
    // Fetch recent duties for the seafarer
    $recentDuties = array(); // Initialize an array to store duties
    $dutySQL = "SELECT * FROM seafarer_duties WHERE EmpID = 20 ORDER BY DutyDate DESC LIMIT 5";
    $dutyResult = mysqli_query($connection, $dutySQL);
    while ($dutyRow = mysqli_fetch_assoc($dutyResult)) {
        $recentDuties[] = $dutyRow;
    }
    $contractDetails = "Contract Details will soon be updated!";
} else {
    // User not found
    echo "User not found";
}
?>

